(101, 'MAIL', 'request-new-password', 'hu', 'Új jelszó igénylése', '<div>Kedves&nbsp;<b>{{ firstName }}</b>!</div><div><br></div><div>Kattintson az alábbi linkre jelszavának módosításához:<br></div><div><br></div><div><a href=\"{link}\" target=\"_blank\">{{ link }}</a><br></div>', 'domain|firstName|lastName|email|link', 'Send password creation link to the user in case of forgotten password.', NULL),
(102, 'MAIL', 'new-password', 'hu', 'Az Ön jelszava a Mentory rendszeréhez 2', '<div>Kedves <b>{{ firstName }}</b>!</div><div><br></div><div>Az az alábbi email címmel regisztrálva lett a <b>careline.hu</b> rendszerébe:</div><div><b>{{ email }}</b><br></div><div><br></div><div>Kattintson az alábbi linkre jelszavának beállításához:<br></div><div><a href=\"{link}\" target=\"_blank\">{{ link }}</a><br></div>', 'domain|firstName|lastName|email|link', 'Send password creation link to the newly added user ', NULL),
(167, 'DOCUMENT', 'doc-transfer', 'hu', 'ÁTADÁS JEGYZŐKÖNYV<br>Használati tárgyakra vonatkozóan', '<p>Alulírott&nbsp;<b>{{ name }}</b> (született: {{ birthplace }}, {{ birthdate }},\r\nanyja neve: {{ mothername }})&nbsp;{{ address }} szám alatti lakos\r\nkijelentem, hogy az alább felsorolt használati tárgyakat a mai napon</p><p>a Careline.hu Kft-től:</p>\r\n\r\n<ul>\r\n<li>{{ carType }} típusú, {{ licenceplate }} forglami rendszámú személygépjárművet, annak forgalmi engedélyét és kulcsát <br>(km óra állása: {{ mileage }} km)</li>\r\n{% if cardNumber %}\r\n<li>a MOL üzemanygkártyát, melynek száma: {{ cardNumber }}, PIN kódja: {{ cardPin }}</li>\r\n{% endif %}\r\n</ul>\r\n<p>hiánytalanul átvettem.</p><p><br></p>\r\n\r\n<p>Budapest, {{ date }}</p><p><br></p>\r\n<p><br></p>\r\n<table width=\"100%\" border=\"0\"><tbody><tr><td width=\"50%\" align=\"center\">László Gábor /h.<br>Carline.hu Kft<br></td><td width=\"50%\" align=\"center\"><p>{{ name }}<br></p></td></tr></tbody>\r\n</table>\r\n<p><br></p>', 'name|birthplace|birthdate|mothername|address|carType|licenceplate|mileage|cardNumber|cardPin|date', 'ÁTADÁS JEGYZŐKÖNYV\r\nHasználati tárgyakra vonatkozóan', 'doc-transfer'),
(170, 'DOCUMENT', 'doc-takeover', 'hu', 'ÁTVÉTELI JEGYZŐKÖNYV<br>Használati tárgyakra vonatkozóan', '<p>Alulírott&nbsp;<b>{{ name }}</b> (született: {{ birthplace }}, {{ birthdate }},\r\nanyja neve: {{ mothername }})&nbsp;{{ address }} szám alatti lakos\r\nkijelentem, hogy az alább felsorolt használati tárgyakat a mai napon <br><br>a Careline.hu Kft-nek:</p>\r\n\r\n<ul><li>{{ carType }} típusú, {{ licenceplate }} forglami rendszámú személygépjárművet, annak forgalmi engedélyét és kulcsát <br>(km óra állása: {{ mileage }} km)</li>\r\n{% if cardNumber %}\r\n<li>a MOL üzemanygkártyát, melynek száma: {{ cardNumber }}, PIN kódja: {{ cardPin }}</li>\r\n{% endif %}\r\n</ul>\r\n<p>hiánytalanul visszaadtam.</p><p><br></p>\r\n\r\n<p>Budapest, {{ date }}</p><p><br></p>\r\n<p><br></p>\r\n<table width=\"100%\" border=\"0\"><tbody><tr><td width=\"50%\" align=\"center\">László Gábor /h.<br>Carline.hu Kft<br></td><td width=\"50%\" align=\"center\"><p>{{ name }}<br></p></td></tr></tbody>\r\n</table>\r\n<p><br><br></p>', 'name|birthplace|birthdate|mothername|address|carType|licenceplate|mileage|cardNumber|cardPin|date', 'ÁTVÉTELI JEGYZŐKÖNYV\r\nHasználati tárgyakra vonatkozóan', 'doc-transfer');
