var shuffleArray = function(array) {
  var currentIndex = array.length, temporaryValue, randomIndex;

  // While there remain elements to shuffle...
  while (0 !== currentIndex) {

    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex -= 1;

    // And swap it with the current element.
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }
  return array;
}

var getSplitTextDirs = function(alen,d) {
	var ri = new Array();

	switch (d) {
		case "forward":
		case "random":
			for (var si=0;si<=alen;si++) { ri.push(si);}
			if (d==="random") ri = shuffleArray(ri);
		break;
		case "backward":
			for (var si=0;si<=alen;si++)	{ ri.push(alen-si); }							
		break;
		case "middletoedge":
			var cc = Math.ceil(alen/2),
				mm = cc-1,
				pp = cc+1;							
			ri.push(cc);														
			for (var si=0;si<cc;si++) {
				if (mm>=0) ri.push(mm);
				if (pp<=alen) ri.push(pp);
				mm--;
				pp++;
			}																	
		break;
		case "edgetomiddle":
			var mm = alen,
				pp = 0;														
			for (var si=0;si<=Math.floor(alen/2);si++) {
				ri.push(mm);
				if (pp<mm) ri.push(pp);
				mm--;
				pp++;
			}																						
		break;		
	}
	
	return ri;
}


